option.value:
@echo off
echo ===================================
echo  REBUILD PROJECT WITH LOMBOK
echo ===================================
echo.

cd /d D:\AnhTran\PROPTIT\Backend_docs\Buoi7\dkmh\dkmh

echo [1/3] Cleaning project...
call mvnw.cmd clean
if %errorlevel% neq 0 (
    echo ERROR: Clean failed!
    pause
    exit /b %errorlevel%
)

echo.
echo [2/3] Compiling project...
call mvnw.cmd compile
if %errorlevel% neq 0 (
    echo ERROR: Compile failed!
    pause
    exit /b %errorlevel%
)

echo.
echo [3/3] Checking generated classes...
if exist "target\classes\com\qlsv\dkmh\dto\request\LopHocPhanRequest.class" (
    echo SUCCESS: LopHocPhanRequest.class generated!
) else (
    echo WARNING: LopHocPhanRequest.class not found!
)

echo.
echo ===================================
echo  BUILD COMPLETE!
echo ===================================
echo.
echo Check target\classes folder for generated .class files
pause
